sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   if(newValue === '1'){
	g_form.showFieldMsg('u_frequency','Take the medicine only one time per day','info');
   }
   else if(newValue === '2'){
	g_form.showFieldMsg('u_frequency','Take the medicine twice per day','info');
   }
   else if(newValue === '3'){
	g_form.showFieldMsg('u_frequency','Take the medicine thrice per day','info');
   }
   //Type appropriate comment here, and begin script below

}
