sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('global.PatientUtilsClient');
	ga.addParam('sysparm_name', 'fetchPatientDetails');
	ga.addParam('sysparm_patient_id', newValue);
	ga.getXMLAnswer(response);

	function response(v) {
		if (!v.error) {
			v=JSON.parse(v);
			g_form.setValue('u_patient_name', v.name);
			g_form.setValue('u_patient_age', v.age);
			g_form.setValue('u_patient_gender', v.gender);
			g_form.setValue('u_vip', v.vip);
		} else {
			g_form.showFieldMsg('u_patient', 'Patient not found', 'error');
		}
	}

	//Type appropriate comment here, and begin script below

}
