sys_script_include.script:
var DoctorUtils = Class.create();
DoctorUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    DoctorInfo: function() {
        var answer = '';
        var DocInfo = new GlideRecord('u_doctor');
        DocInfo.query();
        while (DocInfo.next()) {
            answer += DocInfo.u_first_name.getDisplayValue() + '--' +
                      DocInfo.u_last_name.getDisplayValue() + '--' +
                      DocInfo.u_phone_number.getDisplayValue() + '--' +
                      DocInfo.u_email.getDisplayValue() + '--' +
                      DocInfo.u_specialization.getDisplayValue() + '\n';
        }
        return answer;
    },

    DoctorInfoByID: function(DocID) {
        var answer = '';
        var DocInfobyID = new GlideRecord('u_doctor');
        DocInfobyID.addQuery('u_number', DocID);
        DocInfobyID.query();
        while (DocInfobyID.next()) {
            answer = DocInfobyID.u_first_name.getDisplayValue() + '--' +
                     DocInfobyID.u_last_name.getDisplayValue() + '--' +
                     DocInfobyID.u_phone_number.getDisplayValue() + '--' +
                     DocInfobyID.u_email.getDisplayValue() + '--' +
                     DocInfobyID.u_specialization.getDisplayValue();
        }
        return answer;
    },

    // New Function to fetch doctor by department
    FetchDoctorByDepartment: function(department) {
        var result = [];
        var gr = new GlideRecord('u_doctor');
        gr.addQuery('u_specialization', department); // use sys_id or name based on your implementation
        gr.query();
        while (gr.next()) {
            var doctor = gr.u_first_name.getDisplayValue() + '--' +
                         gr.u_last_name.getDisplayValue() + '--' +
                         gr.u_phone_number.getDisplayValue() + '--' +
                         gr.u_email.getDisplayValue() + '--' +
                         gr.u_specialization.getDisplayValue();
            result.push(doctor);
        }
        return result.join('\n');
    },

    type: 'DoctorUtils'
});
