sys_script_include.script:
var PatientUtilsClient = Class.create();
PatientUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	fetchPatientDetails: function(){
		var patientId = this.getParameter('sysparm_patient_id');
		var gr = new GlideRecord('u_patient_record');
		if(gr.get(patientId)){
			var result = {
				name : gr.u_first_name+ ' ' + gr.u_last_name,
				age : gr.getValue('u_age1').toString(),
				gender : gr.getValue('u_gender').toString(),
				vip : gr.getValue('u_vip').toString()
			};
			return JSON.stringify(result);
		}
		else return JSON.stringify({error : 'Record not found'});
	},
    type: 'PatientUtilsClient'
});
