sys_script_include.script:
var PatientDetailsUtils = Class.create();
PatientDetailsUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    patientDetails : function(){
		var patientId = this.getParameter('sysparm_patient_id');
		var patientGR = new GlideRecord('u_patient_record');
		if(patientGR.get(patientId)){
			var result = {
				name : patientGR.getValue('u_first_name') + " " + patientGR.getValue('u_last_name'),
				age : patientGR.getValue('u_age1'),
				gender : patientGR.getDisplayValue('u_gender'),
				vip : patientGR.getValue('u_vip')
			};
			return JSON.stringify(result);
		}
		return JSON.stringify({ error: "Patient not found" });
	},

    type: 'PatientDetailsUtils'
});
